import "./App.css"
import { useState } from 'react'

// 定义 App 组件
// 组件名称应该使用大写，并和文件名称相同
const App = () => {

  console.log("App 组件渲染了。。。")

  // 1.setState 只有在数据发生变化的时候，才会被调用
  const [counter, setCounter] = useState(1)

  const add = () => {
    setCounter(2)
  }

  return (<div className="app">
    <h1>{counter}</h1>
    <button onClick={add}>+1</button>
  </div>)
}

export default App
